mapper.select:
SELECT COUNT(*) FROM V_MEMBER_A





				AND MEMBER_ID LIKE #{sv}


				AND MEMBER_NICKNAME LIKE #{sv}

SELECT *
FROM V_MEMBER_A





				AND MEMBER_ID LIKE #{sv}


				AND MEMBER_NICKNAME LIKE #{sv}





	ORDER BY MEMBER_STATUS

mapper.update:
UPDATE MEMBER SET
MEMBER_TICKET = MEMBER_TICKET + #{ticket}
WHERE MEMBER_NO = #{memberNo}

UPDATE MEMBER SET
MEMBER_STATUS = 'N'
WHERE MEMBER_NO = #{memberNo}

UPDATE MEMBER SET
MEMBER_STATUS = 'Y'
WHERE MEMBER_NO = #{memberNo}

UPDATE MEMBER SET
MEMBER_ACCOUNT = #{memberAccount}
WHERE MEMBER_NO = #{memberNo}
