mapper.select:
SELECT MEMBER_NO, MEMBER_ID, MEMBER_NICKNAME, MEMBER_PWD, MEMBER_EMAIL, MEMBER_GENDER, MEMBER_GENDER, MEMBER_PHONE, MEMBER_BIRTH, MEMBER_INTRODUCE, MEMBER_PLACE, MEMBER_CATEGORY, MEMBER_PROFILE, MEMBER_SIGNUP_DATE, MEMBER_GRADE, MEMBER_TICKET, MEMBER_ACCOUNT, MEMBER_ID_SORT, MEMBER_STATUS
FROM MEMBER WHERE MEMBER_ID= #{memberId }
AND MEMBER_STATUS = 'Y'

SELECT COUNT(*) FROM V_MEMBER_A





				AND MEMBER_ID LIKE #{sv}


				AND MEMBER_NICKNAME LIKE #{sv}

SELECT *
FROM V_MEMBER_A





				AND MEMBER_ID LIKE #{sv}


				AND MEMBER_NICKNAME LIKE #{sv}





	ORDER BY MEMBER_STATUS

mapper.update:
UPDATE MEMBER SET
MEMBER_TICKET = MEMBER_TICKET + #{ticket}
WHERE MEMBER_NO = #{memberNo}

UPDATE MEMBER SET
MEMBER_STATUS = 'N'
WHERE MEMBER_NO = #{memberNo}

UPDATE MEMBER SET
MEMBER_STATUS = 'Y'
WHERE MEMBER_NO = #{memberNo}

UPDATE MEMBER SET
MEMBER_ACCOUNT = #{memberAccount}
WHERE MEMBER_NO = #{memberNo}

UPDATE MEMBER SET MEMBER_NICKNAME = #{memberNickname},
MEMBER_PROFILE = #{memberProfile},
MEMBER_EMAIL = #{memberEmail}, MEMBER_GENDER = #{memberGender}, MEMBER_PHONE = #{memberPhone}, MEMBER_BIRTH = #{memberBirth}, MEMBER_INTRODUCE = #{memberIntroduce}, MEMBER_PLACE = #{memberPlace}, MEMBER_CATEGORY = #{memberCategory} WHERE MEMBER_NO = #{memberNo}
