SELECT COUNT(*) FROM V_SEARCH



		AND EVENT_CATEGORY LIKE #{searchKey}


		AND EVENT_TITLE LIKE #{sv}

	AND EVENT_STATUS ='Y'

SELECT * FROM V_SEARCH


		AND EVENT_CATEGORY = #{searchKey}



		AND EVENT_TITLE LIKE #{sv}

	AND EVENT_STATUS ='Y'

ORDER BY EVENT_NO

SELECT * FROM V_SEARCH


		AND EVENT_CATEGORY = #{searchKey}



		AND EVENT_TITLE LIKE #{sv}

	AND EVENT_STATUS ='Y'
	AND EVENT_END > SYSDATE

ORDER BY EVENT_NO
